import React, { useState } from 'react';

/**
 * üè° HomePage Component ‚Äì Welcome screen
 */
function HomePage() {
  const [showInfo, setShowInfo] = useState(false);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üëæ Welcome to Cryptorythm</h1>
      <p>The most advanced AI-powered trading and NFT platform.</p>
      <p>Connect your wallet to get started.</p>

      <button 
        onClick={() => setShowInfo(!showInfo)} 
        style={{ marginTop: '1rem', padding: '10px', fontSize: '16px' }}
      >
        {showInfo ? 'Hide Info' : 'Learn More'}
      </button>

      {showInfo && (
        <div style={{ marginTop: '1rem' }}>
          <p>üöÄ Built for crypto traders, NFT lovers, and AI enthusiasts.</p>
          <p>üîê Fully decentralized. Completely customizable.</p>
        </div>
      )}
    </div>
  );
}

export default HomePage;
